import { GOLD_BUYRATE_PENDING, GOLD_BUYRATE_SUCCESS, GOLD_BUYRATE_FAILED, GOLD_SELLRATE_PENDING, GOLD_SELLRATE_SUCCESS, GOLD_SELLRATE_FAILED } from '../Action/type';

let initialize = {
    BuyRate: null,
    BuyRateStatus: false,
    SellRate: null,
    SellRateStatus: false

}
// { BeforeTaxAmt: "100.11", Tax1Amt: "78.71", Tax2Amt: "78.71" },

export default function GoldRate(state = initialize, action) {
    switch (action.type) {
        case GOLD_BUYRATE_PENDING:
            return {
                ...state,
                BuyRateStatus: true

            }
        case GOLD_BUYRATE_SUCCESS:
            console.log('buyRate', action.paylaod)
            return {
                ...state,
                BuyRateStatus: false,
                BuyRate: action.paylaod

            }
        case GOLD_BUYRATE_FAILED:
            return {
                ...state,
                BuyRateStatus: false,
                BuyRate: action.paylaod

            }
        case GOLD_SELLRATE_PENDING:
            return {
                ...state,
                SellRateStatus: true

            }
        case GOLD_SELLRATE_SUCCESS:
            console.log('sellRate', action.paylaod)
            return {
                ...state,
                SellRateStatus: false,
                SellRate: action.paylaod

            }
        case GOLD_SELLRATE_FAILED:
            return {
                ...state,
                SellRateStatus: false,
                SellRate: action.paylaod

            }
        default:
            return state
    }
}
