import { GET_MASTER, GET_MASTER_SUCCESS, GET_MASTER_PENDING} from '../Action/type';

let initialize = {
    masterData: null,
    masterStatus: false,
    mfBank: null,
    mfStatus: false,
    bankRefID: null,

}

export default function Getmaster(state = initialize, action) {
    switch (action.type) {
        case GET_MASTER:
            return {
                ...state,
                masterStatus: true

            }
        case GET_MASTER_SUCCESS:
            return {
                ...state,
                masterStatus: false,
                masterData: action.paylaod

            }
        case GET_MASTER_PENDING:
            return {
                ...state,
                masterStatus: false,
                masterData: action.paylaod

            }

        default:
            return state
    }
}
